class Solution {
public:
    std::vector<int> shortestDistanceAfterQueries(
        int n, std::vector<std::vector<int>>& queries) {
        int q{ static_cast<int>(queries.size()) };

        std::set<int> path;

        for (int i{ 0 }; i < n; ++i) { path.insert(i); }

        std::vector<int> ret;
        ret.resize(q);

        for (int i{ 0 }; i < q; ++i) {
            int u{ queries[i][0] };
            int v{ queries[i][1] };

            for (auto iter{ path.upper_bound(u) }; *iter < v;) {
                iter = path.erase(iter);
            }

            ret[i] = path.size() - 1;
        }

        return ret;
    }
};
