from sortedcontainers import *

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        A = SortedList([[i, i + 1] for i in range(n - 1)])
        res = []
        for i,j in queries:
            si = A.bisect_left([i])
            sj = A.bisect_left([j])
            for k in range(sj - 1, si - 1, -1):
                di, dj = A[k]
                del A[k]
            if si < sj:
                A.add([i,j])
            res.append(len(A))
        return res
