class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> st;
        for(int i=1;i<n-1;i++){
            st.insert(i);
        }
        for(auto x:queries){
            int u=x[0],v=x[1];
            auto it=st.upper_bound(u);
            while(it!=st.end() && *it<v){
                st.erase(*it);
                it=st.upper_bound(u);
            }
            ans.push_back(st.size()+1);
        }
        return ans;
    }
};
