class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;

        for(int i = 0 ; i <n; i++)
            st.insert(i);

        vector<int> ans;

        for(auto q: queries){
            int c1 = q[0], c2 = q[1];

            auto iter = st.find(c1);
            if(iter!=st.end()){
              while((st.upper_bound(c1)!=st.end()) && (*st.upper_bound(c1)) < c2){
                st.erase(st.upper_bound(c1));
              }  
            }

            ans.push_back(st.size() - 1);
        }

        return ans;
    }
};
