class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qs) {
        set<int> st;

        for(int i=0;i<n;++i) {
            st.insert(i);
        }
        vector<int> ans;
        vector<int> rev;
        for(auto c:qs) {
            int f = c[0];
            int t = c[1];
            rev.clear();


          //  if(st.count(f+1)) {
                auto it = st.lower_bound(f+1);
          //     cout << "find " << (*it) << endl;
                while((*it)<t){
                     rev.push_back(*it);
          //          cout << (*it) << " removed" << endl;
                     ++it;
                }
                for(int u:rev) {
                    st.erase(u);
                }

         //   }
            ans.push_back(st.size()-1);
        }
        return ans;

    }
};
