class Node:
    def __init__(self, val):
        self.val = val
        self.next = None

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        removed = [False for i in range(n)]

        nodes = [Node(i) for i in range(n)]

        for i in range(n - 1):
            nodes[i].next = nodes[i+1]

        nodes_left = n
        ans = []
        for query in queries:
            start = query[0]
            end = query[1]

            if not removed[start] and not removed[end]:
                while nodes[start].next.val != end:
                    removed[nodes[start].next.val] = True
                    nodes[start].next = nodes[start].next.next
                    nodes_left -= 1

            ans.append(nodes_left - 1)

        return ans
