function shortestDistanceAfterQueries(n: number, queries: number[][]): number[] {
    const res = [];
    let len = n - 1;
    const deleted = new Set<number>();
    const nodes = Array.from({length: n}, (_, i) => i + 1);
    for (let [start, end] of queries) {
        if (deleted.has(start) || deleted.has(end) || end <= nodes[start]) {
            res.push(len);
            continue;
        }

        let cur = nodes[start];
        while (cur < end) {
            len--;
            deleted.add(cur);
            cur = nodes[cur];
        }

        nodes[start] = end;
        res.push(len);
    }

    return res
};
