from typing import List

from sortedcontainers import SortedList


class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ans = []
        points = SortedList(range(n))
        for x, y in queries:
            idx = points.bisect_left(x)
            if points[idx] == x and y > points[idx + 1]:
                idx2 = points.bisect_left(y)
                for i in range(idx2 - idx - 1):
                    points.pop(idx + 1)
            ans.append(len(points) - 1)
        return ans
