/**
 * @param {number} n
 * @param {number[][]} queries
 * @return {number[]}
 */
var shortestDistanceAfterQueries = function (n, queries) {
  let map = new Map();
  let ans = [];
  for (let i = 0; i < n - 1; i++) {
    map.set(i, i + 1);
  }
  let fun = ([f, t]) => {
    if (!map.has(f) || map.get(f) > t) return;
    for (let i = f; i < t; ) {
      let t = map.get(i);
      map.delete(i);
      i = t;
    }
    map.set(f, t);
  };

  for (let q of queries) {
    fun(q);
    ans.push(map.size);
  }
  return ans;
};
