class Solution {
public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    vector<int> ans;
    int len = n - 1;
    set<pair<int, int>> s;
    for (auto v : queries)
    {
      int x = v[0], y = v[1];
      auto u = s.lower_bound(make_pair(x, -y));
      if (u != begin(s) && -prev(u)->second >= y);
      else
      {
        while (u != end(s))
        {
          auto [xx, yy] = *u;
          yy = -yy;
          if (xx >= y)
            break;
          u++;
          s.erase({xx, -yy});
          len += yy - xx - 1;
        }
        len -= y - x - 1;
        s.insert({x, -y});
      }
      ans.push_back(len);
    }
    return ans;
  }
};
