class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
      map<int, int> m; for(int i = 0; i<n-1; ++i) m[i] = 1;
      vector<int> ans;
      for(vector<int>&q: queries){
        int a = q[0], l = q[1] - a, b = q[1];
        auto it = m.find(a);
        if(it!= m.end() && it->second < l) {
          it->second = l;
          for(it = next(it); it!= m.end() && it->first<b; ) {
            it = m.erase(it);
          }
        }
        ans.emplace_back(m.size());
      }
      return ans;
    }
};
