class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
                set<int> s;

        for(int i = 0 ; i <n ;i++){
          s.insert(i);
        }
        vector<int> ans;
        for(auto it:queries){
            int x = it[0] , y = it[1];
            auto st = s.find(it[0]) , en = s.find(it[1]);
            if(st == s.end() or en == s.end()){
                ans.push_back(s.size()-1);continue;
            }
            st = next(st);
             while(true){
                 auto it = next(st);
                 if(st == en){
                     break;
                 } 
                 s.erase(st);
                 st = it;
             }
            ans.push_back(s.size()-1);
            }
        return ans;
        }
    };
