class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; i++) {
            s.insert(i);
        }

        vector<int> ans;
        int dist = n - 1;
        for (int i = 0; i < queries.size(); i++) {
            int from = queries[i][0], to = queries[i][1];
            set<int>::iterator it = s.find(from);

            if (it != s.end()) {
                if (s.find(to) != s.end()){
                    it++;
                    while (*it != to) {
                        it = s.erase(it);
                        dist--;
                    }
                }
            }
            ans.push_back(dist);
        }
        return ans;
    }
};
