from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        l = SortedList([[i,i+1] for i in range(n)])

        curr = n-1

        res = []

        for s,e in queries:
            inds = bisect.bisect_left(l, [s,s])
            inde = bisect.bisect_right(l, [e,float('inf')])

            # print(curr, inds, inde, l)

            if inds >= inde -1:
                res += [curr]
            else:
                curr -= inde - inds - 2
                merged = [s,e]

                for i in range(inds, inde):
                    if i!=inde -1:
                        l.remove(l[inds])
                l.add(merged)
                res += [curr]
            # print(l)

        return res
