class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        edges = {}
        for i in range(n-1):
            edges[i] = i+1

        ans = []
        for u, v in queries:
            if u in edges:
                # if found better edge
                if edges[u] < v:
                    # remove all nodes in between
                    for i in range(edges[u], v):
                        if i in edges:
                            del edges[i]
                    edges[u] = v

            ans.append(len(edges))

        return ans
