class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> mp;
        for (int i = 0; i < n - 1; i++) mp[i] = i + 1;
        vector<int> ret;
        for (auto &q: queries) {
            int l = q[0], r = q[1];
            auto it = mp.lower_bound(l);
            if (it != mp.begin()) it--;
            while (it != mp.end()) {
                if (it->second <= l) {
                    it++;
                    continue;
                }
                if (it->first >= r) break;
                mp.erase(it++);
            }
            mp[l] = r;
            ret.emplace_back(mp.size());
        }
        return ret;
    }
};
