struct ListN {
    int val;
    ListN* next;

    ListN():val(0),next(0) {}
    ListN(int a, ListN* b):val(a),next(b){}
};

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qs) {
        int m = qs.size();
        vector<int> res(m);
        ListN* p;
        ListN* q = 0;
        map<int,ListN*> ma;
        for (int i = n-1; i >= 0; --i) {
            p = new ListN(i, q);
            ma[i] = p;
            q = p;
            p = 0;
        }

        for (int i = 0; i < m; ++i) {
            int a = qs[i][0];
            int b = qs[i][1];
            if (ma.count(a)) {
                ListN* c = ma[a];
                ListN* d = ma[b];
                ListN* e = c->next;
                while (e && e != d) {
                    ma.erase(e->val);
                    e = e->next;
                }
                c->next = e;
            }
            res[i] = ma.size() - 1;
        }

        return res;
    }
};
