class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int cnt = n - 1;
        vector<int> vis(n+1,0);
        vector<int> par(n+1,0);
        for (int i=1;i<=n-1;i++){
            par[i-1] = i;
        }
        vector<int> ans;
        for (auto it : queries){
            int a = it[0];
            int b = it[1];
            if (vis[a] || vis[b]){
                ans.push_back(cnt);
                continue;
            }
            int temp = par[a];
            while (temp != b){
                vis[temp] = 1;
                temp = par[temp];
                cnt--;
            }
            par[a] = b;
            ans.push_back(cnt);
        }
        return ans;
    }
};
