class ListNode:
    def __init__(self,val=0,next=None):
        self.val=val
        self.next=next

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        res=[]
        size=n-1
        head=None
        hmap={}
        for i in range(n-1,-1,-1):
            head=ListNode(i,head)
            hmap[i]=head
        for a,b in queries:
            if a not in hmap:
                res.append(size)
                continue
            node=hmap[a]
            while node.next.val<b:
                del hmap[node.next.val]
                node.next=node.next.next
                size-=1
            res.append(size)
        return res
