class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; i++) {
            s.insert(i);
        }
        vector<int> ans;
        for (auto &q : queries) {
            int from = q[0];
            int to = q[1];
            vector<int> removed;
            for (auto ite = s.upper_bound(from); ite != s.end(); ++ite) {
                int v = *ite;
                if (v >= to) break;
                removed.push_back(v);
            }
            for (auto x : removed) s.erase(x);
            ans.push_back((int)s.size() - 1);
        }
        return ans;
    }
};
