class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; ++i) {
            s.insert(i);
        }
        vector<int> ans;
        for (const auto &q : queries) {
            const auto it1 = s.upper_bound(q[0]);
            const auto it2 = s.lower_bound(q[1]);
            auto it = it1;
            while (it != it2) s.erase(it++);
            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};
