class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> m;
        vector<int> res;

        for (int i = 0; i < n; i++) {
            m[i] = i + 1;
        }

        for (const auto& it : queries) {
            int u = it[0];
            int v = it[1];

            auto start = m.upper_bound(u);
            auto end = m.lower_bound(v);

            m.erase(start, end);

            res.push_back(m.size() - 1);
        }

        return res;
    }
};
