class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> Set;
        for(int i=0;i<n-1;i++)Set.insert(i);
        vector<int> ans;
        for(auto &x : queries){
            auto it=Set.upper_bound(x[0]);
            set<int> erase;
            while(it!=Set.end() && *it<x[1]){
                erase.insert(*it);
                it++;
            }
            for(auto &x : erase)
                Set.erase(x);
            ans.push_back(Set.size());
        }
        return ans;
    }
};
