import java.util.ArrayList;
import java.util.Arrays;
import java.util.Comparator;
import java.util.PriorityQueue;

class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {

        int qn=queries.length;
        int[] ans=new int[qn];

        int dist=n-1;
        int[]f=new int[n];
        for(int i=0;i<n;i++){
            f[i]=i+1;
        }


        for(int i=0;i<qn;i++){
            int u=queries[i][0];
            int v=queries[i][1];

            if (f[u] != f[v]) {
                int ori = 0;
                for (int j = u; j < v; ) {
                    int nj = f[j];
                    f[j]=v;

                    j = nj;
                    ori++;
                }

                dist -= ori - 1;
            }


            ans[i]=dist;

        }

        return ans;
    }



}
