class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for (int i = 0; i < n; i++)
            st.insert(i);

        vector<int> ans;
        for (auto &q : queries) {
            int start = q[0] + 1, end = q[1] - 1;
            while (st.size() && start <= end) {
                auto x = st.lower_bound(start);
                if (x == st.end() || *x > end || *x == n - 1)
                    break;
                st.erase(x);
            }

            // for (int i : st)
            //     cout << i << " ";
            // cout << endl;

            ans.push_back(max(0, int(st.size()) - 1));
        }
        return ans;
    }
};
