class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int cans = n - 1;
        set<int>st;
        for(int i = 0; i < n; i++){
            st.insert(i);
        }
        vector<int>ans;
        for(auto q : queries){
            int u = q[0], v = q[1];
            if(u >= v){
                ans.push_back(cans);
                continue;
            }
            auto lb = st.upper_bound(u);
            while(lb != st.end() and *lb < v){
                int val = *lb;
                lb++;
                st.erase(val);
            }
            cans = st.size() - 1;
            ans.push_back(cans);
        }
        return ans;
    }
};
