class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans; 

        set<int> s;
        for(int i = 0; i < n; i++)
        {
            s.insert(i);
        }

        for(auto &q:queries)
        {
            int f = q[0], t = q[1];

            for(int i = f+1; i < t; i++)
            {
                auto it = s.find(i);
                if (it != s.end()) {
                    s.erase(i);
                } else {
                    it = s.upper_bound(i);
                    if (it != s.end()) {
                        i = *it;
                        if(i < t) s.erase(i);
                    } else {
                        break;
                    }
                }
            }

            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};
