class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i<n; ++i)
            s.insert(i);
        vector<int> ans;
        for (auto x : queries) {
            if (s.find(x[0])!=s.end()) {
            while(1) {
                auto it =s.upper_bound(x[0]);
                if (it!=s.end()&&*it<x[1])
                    s.erase(it);
                else
                    break;
            }}
            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};
