class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int z=n-1;
        set<pair<int, int>> st;
        vector<int> res;
        for (int i=0; i<q.size(); ++i) {
            int a=q[i][0], b=q[i][1];
            auto x=st.lower_bound({a, -1});

            if (x!=st.begin()) {
                x=prev(x);
            }
            bool ok=false;
            while (x!=st.end()) {
                int j=x->first, k=x->second;
                if (j<=a&&k>=b) {
                    res.push_back(z);
                    ok=true;
                    break;
                }
                x=next(x);
                if (j>=a&&k<=b) {
                    z+=k-j-1;
                    st.erase(prev(x));
                }
                if (j>=b) {
                    break;
                }
            }
            if (!ok) {
                z-=b-a-1;
                st.insert({a, b});
                res.push_back(z);
            }
        }
        return res;
    }
};
