class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> f(n + 1);
        for(int i = 0; i <= n; ++i) f[i] = i + 1;
        int ans = 0;
        vector<int> res;
        for(auto a : queries)
        {
            int u = a[0], v = a[1];
            while(f[u] < v)
            {
                //cout<<u<<' '<<v<<endl;
                int tu = f[u];
                f[u] = v;
                u = tu;
                ans++;
            }
            res.push_back(n - 1  - ans);
        }
        return res;
    }
};
