class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for (int i = 0; i < n; ++i) st.insert(i);
        vector<int> res;
        for (auto &q: queries) {
            int x = q[0], y = q[1];
            auto it = st.upper_bound(x);
            while (it != st.end() && *it < y) {
                it = st.erase(it);
            }
            res.push_back(st.size() - 1);
        }
        return res;
    }
};
