import sortedcontainers
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        res = []

        lst = sortedcontainers.SortedList()

        for i in range(n):
            lst.add(i)

        for u, v in queries:
            #print(lst)
            p = lst.bisect_right(u)
            remove = []
            while p < len(lst) and lst[p] < v:
                remove.append(lst[p])
                p += 1
            #remove.append(v)
            for r in remove:
                lst.remove(r)
            res.append(len(lst) - 1)

        return res
