class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:


        R = []
        for i in range(n-1):
            R.append((i,i+1))
        #print(R)

        res = []
        for q in queries:
            q1,q2 = q[0],q[1]
            left,right = 0,len(R)-1
            while left <= right:
                mid = (left+right)//2
                if R[mid][0] <= q1:
                    left =mid+1
                elif R[mid][0] > q1:
                    right = mid-1
            Left_index = right
            left,right = 0,len(R)-1
            while left <= right:
                mid = (left+right)//2
                if R[mid][1] < q2:
                    left = mid+1
                elif R[mid][1] >= q2:
                    right = mid-1
            Right_index = left
            #print(Left_index,Right_index)
            if Right_index != Left_index:
                v1,v2 = R[Left_index][0],R[Right_index][1]
                del R[Left_index:Right_index+1]
                R.insert(Left_index,(v1,v2))
            res.append(len(R))
            #print(R)

        return res
