class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> intervals;
        vector<int> ans;
        ans.reserve(n);
        for (int i = 0; i < n - 1; i++) {
            intervals.emplace(i);
        }
        for (const auto& query: queries) {
            auto it = intervals.find(query[0]);
            if (it != intervals.end()) {
                it++;
                while (it != intervals.end() && *it < query[1]) {
                    it = intervals.erase(it);
                }                
            }

            ans.emplace_back(intervals.size());
        }
        return ans;
    }
};
