class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        nodes = [i for i in range(1, n + 1)]
        totalPath = n - 1
        ans = []
        for start, end in queries:
            if nodes[start] == -1 or nodes[start] > end:
                ans.append(totalPath)
            else:
                decrementedCount = 0
                curr = nodes[start]
                while curr != end:
                    prevCurr = curr
                    curr = nodes[curr]
                    nodes[prevCurr] = -1
                    decrementedCount += 1
                totalPath -= decrementedCount
                nodes[start] = end
                ans.append(totalPath)
        return ans
