class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int,int>M;
        M[-100000]=-1000000;
        M[100000]=100000;
        int ans=n-1;
        vector<int>res;
        for(int i=0;i<n-1;i++)M[i]=i+1;
        for(auto &t:queries){
            int l=t[0],r=t[1];
            while(1){
                auto it=M.lower_bound(l);
                // cout<<it->first<<" "<<it->second<<endl;
                if(it->first>=l&&it->second<=r){
                    ans-=1;
                    M.erase(it);
                    continue;
                }
                if(it->first<=l&&it->second>=r){
                    break;
                }
                it--;
                                if(it->first<=l&&it->second>=r){
                    break;
                }
                M[l]=r;
                ans+=1;
                break;
            }
            res.push_back(ans);
        }
        return res;
    }
};
