from sortedcontainers import SortedList


class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        intervals = SortedList(list(range(n - 1)))
        ans = []
        for u, v in queries:
            rank = intervals.bisect_left(u + 1)
            while rank < len(intervals) and intervals[rank] < v:
                del intervals[rank]
            ans.append(len(intervals))
        return ans
