class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        s.insert(0);
        for(int i=0; i<n-1; i++) {
            s.insert(i+1);
        }

        vector<int> ans;
        for(auto q:queries) {
            auto start = s.lower_bound(q[0]);

            if(start != s.end() && *start == q[0]) {
                auto it = next(start);
                while(*it < q[1]) {
                    s.erase(*it);
                    start = s.lower_bound(q[0]);
                    it = next(start);
                }
            }

            ans.push_back(s.size() - 1);
        }

        return ans;

    }
};
