class Solution {
public:
    int nxt[100005];
    int ok[100005];
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        for(int i=0;i<n;i++){
            nxt[i] = i+1;
            ok[i] = true;
        }

        vector<int> a;
        int ans = n-1;
        for(auto v: queries){
            int x = v[0], y = v[1];
            if(x >= y) {a.push_back(ans); continue;}
            if(!ok[x] || !ok[y]) {a.push_back(ans); continue;}
            int p = nxt[x];
            int cnt = 5;
            while(cnt && p != y){
                ans --;
                ok[p] = false;
                p = nxt[p];
                // cout << p << endl;
            }
            nxt[x] = y;
            a.push_back(ans);
        }
        return a;

    }
};
