class Solution {
public:
    Solution(){
        ios_base::sync_with_stdio(0);
        cin.tie(0);
        cout.tie(0);
    }
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i=0;i<n;i++){
            st.insert(i);
        }
        int sz=queries.size();
        vector<int> ans;
        for(int i=0;i<sz;i++){
            if(st.count(queries[i][0]) && st.count(queries[i][1])){
                st.erase(next(st.find(queries[i][0]),1),st.find(queries[i][1]));
            }
            // for(auto& e:st) cout<<e<<' ';
            // cout<<'\n';
            ans.push_back(st.size()-1);
        }
        // cout<<'\n';
        return ans;
    }
};
