class Solution {
public:
    class LL{
        public:
        int val;
        LL*next;
        LL(int num){
            val = num;
            next=nullptr;
        }
    };
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int,int> u;
        vector<LL*> nodes;
        int dist=n-1;
        vector<int> ans;
        for(int i=0;i<n;i++){
            LL* now = new LL(i);
            if(i>0){
                nodes.back()->next = now;
            }
            nodes.push_back(now);
        }
        for(int i=0;i<queries.size();i++){
            if(u[queries[i][0]] || u[queries[i][1]]){
                ans.push_back(dist);
                continue;
            }
            int num1=queries[i][0],num2=queries[i][1];
            int less=0;
            while(nodes[num1]->next!=nodes[num2]){
                less++;
                u[nodes[num1]->next->val]++;
                nodes[num1]->next = nodes[num1]->next->next;
            }
            dist-=less;
            ans.push_back(dist);
        }
        return ans;
    }
};
