from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:        
        container = SortedList()

        for i in range(n):
            container.add(i)

        res = []
        for u, v in queries:
            pos = container.bisect_right(u)
            while pos < len(container) and container[pos] < v:
                container.remove(container[pos])

            res.append(len(container) - 1)

        return res
