class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        seen = { i : i + 1 for i in range(n - 1) }

        res = []
        for start, end in queries:
            while start in seen and seen[start] < end: 
                if seen[start] in seen:
                    tmp = seen[seen[start]] 
                    del seen[seen[start]]
                    seen[start] = tmp
            res.append(len(seen))

        return res
