class Solution {
public:
    int fa[100005];
    int fd(int x){
        return x == fa[x] ? x : fa[x] = fd(fa[x]);
    }
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int ans = n - 1;
        std::vector <int> res;
        std::set <int> ext;
        for(int i = 1; i <= n - 1; ++i) ext.insert(i);
        for(int i = 1; i <= n - 1; ++i) fa[i] = i;
        for(int i = 0; i < queries.size(); ++i){
            int x = queries[i][0], y = queries[i][1];
            x++, y++;
            if(fd(x) != fd(y - 1)){
                auto it = ext.lower_bound(x + 1);
                while(it != ext.end()){
                    int t = *it;
                    if(t > y - 1) break;
                    if(fd(t) != fd(x)){
                        ans -= 1;
                        fa[t] = fd(x);
                    }
                    ++it;
                    ext.erase(t);
                }
            }
            res.push_back(ans);
        }
        return res;
    }
};
