from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl = SortedList()
        for i in range(n):
            sl.add(i)
        print(sl)
        ans = []

        for x, y in queries:
            if x in sl:
                idx = sl.index(x)
                while sl[idx + 1] < y:
                    sl.pop(idx + 1)

            ans.append(len(sl) - 1)

        return ans
