class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        int to[n];
        set<int> sets;
        for(int i=0;i<n;i++)
            sets.insert(i);
        for(auto query:queries)
        {
            int x=query[0],y=query[1];
            auto itx=sets.find(x);
            auto ity=sets.find(y);
            if(itx==sets.end()||ity==sets.end())
                ans.push_back(sets.size()-1);
            else
            {
                itx++;
                sets.erase(itx,ity);
                ans.push_back(sets.size()-1);
            }
        }
        return ans;
    }
};
