class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i = 0; i < n; i++){
            s.insert(i);
        }
        vector<int> ans;
        for(int i = 0; i < queries.size(); i++){
            int k = queries[i][0];
            int l = queries[i][1];
            auto it = s.lower_bound(k);

            if (*it == k){
                vector<int> rem;
                it++;
                while(*it < l){
                    rem.push_back(*it);
                    it++;
                }
                for(int m: rem){
                    s.erase(m);
                }
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
