class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> pointers(n); // {prev, next}
        vector<bool> touches(n, true);
        for (int i=0; i<n; ++i) pointers[i] = i + 1;

        int path = n - 1;
        vector<int> ans;
        for (int i=0; i<queries.size(); ++i) {
            int p = queries[i][0];
            int q = queries[i][1];
            if (!touches[p]) {
                ans.push_back(path);
                continue;
            }
            int org = p;
            p = pointers[p];
            while (p < q) {
                --path;
                touches[p] = false;
                p = pointers[p];
            }
            // for (int j=0; j<n; ++j) cout << pointers[j] << " \n"[j==n-1];
            // cout << org << ' ' << q << endl;
            pointers[org] = q;
            ans.push_back(path);
        }
        return ans;


    }
};
