class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> prox(n), alive(n, 1);
        for (int i = 0; i < n - 1; ++i) prox[i] = i + 1;
        vector<int> ans;
        int cur = n-1;
        for (auto v : queries) {
            int l = v[0], r = v[1];
            if (alive[l] == 0 || alive[r] == 0) {
                ans.push_back(cur);
                continue;
            }
            int t = prox[l];
            while (t != r) alive[t] = 0, t = prox[t], cur--;
            prox[l] = r;
            ans.push_back(cur);
        }
        return ans;
    }
};
