#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> res;
        set<int> st;
        for (int i = 0; i < n; ++i) {
            st.insert(i);
        }
        for(auto &q : queries) {
            int u = q[0], v = q[1];
            auto l = st.upper_bound(u);
            auto r = st.lower_bound(v);
            st.erase(l, r);
            res.push_back((int)st.size() - 1);
        }
        return res;
    }
};
